import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsxRuntime classic */
/* @jsx mdx */
import { LayoutDocs } from 'components/LayoutDocs';

import { Alert, ActionIcon, Image, BackgroundImage, Switch, Text, Title, Group, Space } from '@mantine/core';
import { InfoCircledIcon, GitHubLogoIcon } from '@radix-ui/react-icons'
import ReleaseDate from '../../components/blog/changelog/ReleaseDate'
export const meta = {
  "id": "0-5-5",
  "title": "Version 0.5.5",
  "date": "April 16th, 2022"
};

const layoutProps = {
  meta
};
const MDXLayout = ({ children, ...props }) => (
  <LayoutDocs meta={meta} {...props}>{children}</LayoutDocs>
)
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">




    <ReleaseDate meta={meta} mdxType="ReleaseDate" />
    <hr />
    <Alert icon={<InfoCircledIcon mdxType="InfoCircledIcon" />} title="Warning" color="yellow" mdxType="Alert">
Do not download the Prism package yet. It is currently unstable.
    </Alert>
    <h2 {...{
      "id": "new-features-and-components-grin"
    }}>{`New features and components üòÅ`}<a parentName="h2" {...{
        "href": "#new-features-and-components-grin",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <h3 {...{
      "id": "components"
    }}><em parentName="h3">{`Components:`}</em><a parentName="h3" {...{
        "href": "#components",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h3>
    <p><a parentName="p" {...{
        "href": "/docs/core/action-icon"
      }}>{`ActionIcon`}</a>{` component:`}</p>
    <Group mdxType="Group">
    <ActionIcon variant='default' mdxType="ActionIcon">+</ActionIcon>
    <ActionIcon variant='filled' color='blue' size='xl' mdxType="ActionIcon">
        <GitHubLogoIcon mdxType="GitHubLogoIcon" />
    </ActionIcon>
    </Group>
    <Space h='md' mdxType="Space" />
    <p><a parentName="p" {...{
        "href": "/docs/core/image"
